package org.nmeict.smvdu.Beans;

import java.util.List;

import javax.faces.bean.ManagedBean;
import javax.faces.bean.RequestScoped;

import org.nmeict.smvdu.Beans.SpringClassFile.IOrgLogoProfile;
import org.nmeict.smvdu.Beans.SpringClassFile.OrgLogoProfileService;


@ManagedBean(name="orgLogoDetails")
@RequestScoped
/**
 * OrgLogoDetails generated by hbm2java
 */
public class OrgLogoDetails  implements java.io.Serializable {


     private String orgId;
     private OrgProfile orgProfile;
     private String orgWeb;
     private byte[] orgLogo;
     private String logoFormat;
     private String fileName;
     private String orgName;
     private IOrgLogoProfile iOrgLogoProfile = new OrgLogoProfileService();
    public OrgLogoDetails() {
    }

	
    public OrgLogoDetails(String orgId, OrgProfile orgProfile, String orgWeb) {
        this.orgId = orgId;
        this.orgProfile = orgProfile;
        this.orgWeb = orgWeb;
    }
    public OrgLogoDetails(String orgId, OrgProfile orgProfile, String orgWeb, byte[] orgLogo, String logoFormat, String fileName) {
       this.orgId = orgId;
       this.orgProfile = orgProfile;
       this.orgWeb = orgWeb;
       this.orgLogo = orgLogo;
       this.logoFormat = logoFormat;
       this.fileName = fileName;
    }
   
    public String getOrgId() {
        return this.orgId;
    }
    
    public void setOrgId(String orgId) {
        this.orgId = orgId;
    }
    public OrgProfile getOrgProfile() {
        return this.orgProfile;
    }
    
    public void setOrgProfile(OrgProfile orgProfile) {
        this.orgProfile = orgProfile;
    }
    public String getOrgWeb() {
        return this.orgWeb;
    }
    
    public void setOrgWeb(String orgWeb) {
        this.orgWeb = orgWeb;
    }
    public byte[] getOrgLogo() {
        return this.orgLogo;
    }
    
    public void setOrgLogo(byte[] orgLogo) {
        this.orgLogo = orgLogo;
    }
    public String getLogoFormat() {
        return this.logoFormat;
    }
    
    public void setLogoFormat(String logoFormat) {
        this.logoFormat = logoFormat;
    }
    public String getFileName() {
        return this.fileName;
    }
    
    public void setFileName(String fileName) {
        this.fileName = fileName;
    }


	public String getOrgName() {
		return orgName;
	}


	public void setOrgName(String orgName) {
		this.orgName = orgName;
	}

    
	public IOrgLogoProfile getiOrgLogoProfile() {
        return iOrgLogoProfile;
    }

    public void setiOrgLogoProfile(IOrgLogoProfile iOrgLogoProfile) {
        this.iOrgLogoProfile = iOrgLogoProfile;
    }
    
    
    public List<OrgLogoDetails> details(String query)
    {
        return getiOrgLogoProfile().AllCollegeLogoDetails(query);
    }

}


